import{_ as a,c as o,a4 as t,o as r}from"./chunks/framework.CyeRpf1z.js";const i="/domain/eu/eu-01.png",p="/domain/eu/eu-02.png",l="/domain/eu/eu-03.png",n="/domain/eu/eu-04.png",s="/domain/eu/eu-05.png",d="/domain/eu/eu-06.png",c="/domain/eu/eu-07.png",u="/domain/eu/eu-08.png",m="/domain/eu/eu-09.png",g="/domain/eu/eu-10.png",h="/domain/eu/eu-11.png",_="/domain/eu/eu-12.png",f="/domain/eu/eu-13.png",b="/domain/eu/eu-14.png",y="/domain/eu/eu-15.png",k="/domain/eu/eu-16.png",w="/domain/eu/eu-17.png",$=JSON.parse('{"title":"eu.org注册免费域名","description":"","frontmatter":{},"headers":[],"relativePath":"yiov/website/domain/eu.md","filePath":"yiov/website/domain/eu.md","lastUpdated":1734431113000}'),z={name:"yiov/website/domain/eu.md"};function q(v,e,P,N,x,S){return r(),o("div",null,e[0]||(e[0]=[t('<h1 id="eu-org注册免费域名" tabindex="-1">eu.org注册免费域名 <a class="header-anchor" href="#eu-org注册免费域名" aria-label="Permalink to &quot;eu.org注册免费域名&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">(当前界面文档全部来自yiov.top)</p><ul><li><a href="https://yiov.top/" target="_blank" rel="noreferrer">yiov</a>「伊奥&#39;s 教程集」</li></ul></div><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p>从1996开始提供免费域名, 对个人和组织提供免费的子域名注册</p><p>官网：<a href="https://nic.eu.org/" target="_blank" rel="noreferrer">https://nic.eu.org/</a></p><p><img src="'+i+'" alt="" loading="lazy"></p><h2 id="特色" tabindex="-1">特色 <a class="header-anchor" href="#特色" aria-label="Permalink to &quot;特色&quot;">​</a></h2><ul><li><p>可选类型：<code>*.eu.org</code></p></li><li><p>账号限制：一个账号可申请无数个（需审核通过）</p></li><li><p>提供需求：不需要信用卡、手机号</p></li><li><p>能否托管到CF：能</p></li><li><p>推荐指数：⭐⭐⭐⭐</p></li></ul><h2 id="注册" tabindex="-1">注册 <a class="header-anchor" href="#注册" aria-label="Permalink to &quot;注册&quot;">​</a></h2><p>在 <a href="https://nic.eu.org/" target="_blank" rel="noreferrer">官网</a> 点击 <code>here</code> 注册</p><details class="details custom-block"><summary>官网打不开，显示503</summary><p>网站正常更新维护，等一会再来就行了</p></details><p><img src="'+p+'" alt="" loading="lazy"></p><p>点击 <code>Register</code> 注册</p><p><img src="'+l+'" alt="" loading="lazy"></p><p>用 <a href="https://www.haoweichi.com/Others/ying_guo_shen_fen_sheng_cheng" target="_blank" rel="noreferrer">好维特</a> 生成一个虚拟的英国人信息，当然美国也行</p><p><img src="'+n+'" alt="" loading="lazy"></p><p>按照生成的信息填写，邮箱用自己的就行，建议谷歌邮箱</p><p><img src="'+s+'" alt="" loading="lazy"></p><p>提示需要到邮箱验证激活</p><p><img src="'+d+'" alt="" loading="lazy"></p><p>点击邮箱中的链接激活</p><p><img src="'+c+'" alt="" loading="lazy"></p><p>这里一定要点 <code>Validate</code>，显示 <code>you contact ... now valid.</code> 即可</p><p><img src="'+u+'" alt="" loading="lazy"></p><p>登录我们的账户，账户名是 <code>***-FREE</code></p><p><img src="'+m+'" alt="" loading="lazy"></p><h2 id="申请" tabindex="-1">申请 <a class="header-anchor" href="#申请" aria-label="Permalink to &quot;申请&quot;">​</a></h2><p>登录账号后，点 <code>New Domain</code></p><p><img src="'+g+'" alt="" loading="lazy"></p><p>填写你想申请的完整域名，比如： <code>**.eu.org</code></p><p><img src="'+h+'" alt="" loading="lazy"></p><p>由于官网不提供域名解析，我们借助 <a href="https://www.dnspod.cn/" target="_blank" rel="noreferrer">腾讯的DNSPod</a> 来生成</p><p>点控制台，微信扫码登录账号</p><details class="details custom-block"><summary>可以用其他DNS解析吗</summary><p>可以，喜欢哪个用哪个，也可以用Cloudflare</p></details><p><img src="'+_+'" alt="" loading="lazy"></p><p>点击 我的域名 - 添加域名</p><p><img src="'+f+'" alt="" loading="lazy"></p><p>输入你要申请的域名，或者任意，目的只是为了获取DNS</p><p><img src="'+b+'" alt="" loading="lazy"></p><p>鼠标移动到未解析处，会显示2个DNS</p><p><img src="'+y+'" alt="" loading="lazy"></p><p>回到eu.org底部，选择 <code>server names</code> ，分别粘贴两个DNSPod获取的值，点 <code>Submit</code> 提交</p><p><img src="'+k+'" alt="" loading="lazy"></p><p>提示 <code>No error</code> 没有错误即可，耐心等待审核通过</p><div class="tip custom-block"><p class="custom-block-title">审核需要多久</p><p>有可能几天，有可能几个月，也有可能几年😂</p></div><p><img src="'+w+'" alt="" loading="lazy"></p><h2 id="使用" tabindex="-1">使用 <a class="header-anchor" href="#使用" aria-label="Permalink to &quot;使用&quot;">​</a></h2><p>申请通过后，会发邮件给你</p><p>我们可以仍然使用 <a href="https://www.dnspod.cn/" target="_blank" rel="noreferrer">DNSPod</a> ，或者使用 <a href="./../cloudflare.html">Cloudflare</a> 托管</p><h2 id="特别鸣谢" tabindex="-1">特别鸣谢 <a class="header-anchor" href="#特别鸣谢" aria-label="Permalink to &quot;特别鸣谢&quot;">​</a></h2><ul><li><p><a href="https://www.haoweichi.com/Others/ying_guo_shen_fen_sheng_cheng" target="_blank" rel="noreferrer">好维特虚拟信息生成</a></p></li><li><p><a href="https://www.dnspod.cn/" target="_blank" rel="noreferrer">腾讯的DNSPod</a></p></li></ul>',51)]))}const C=a(z,[["render",q]]);export{$ as __pageData,C as default};
