import{_ as i,c as e,a4 as t,o as l}from"./chunks/framework.CyeRpf1z.js";const a="/shell/termius/termius-01.png",o="/shell/termius/termius-02.png",r="/shell/termius/ios/ios-01.png",p="/shell/termius/win/win-01.png",n="/shell/termius/win/win-02.png",m="/shell/termius/win/win-03.png",c="/shell/termius/win/win-04.png",d="/shell/termius/win/win-05.png",u="/shell/termius/win/win-06.png",g="/shell/termius/win/win-07.png",h="/shell/termius/win/win-08.png",_="/shell/termius/win/win-09.png",w="/shell/termius/win/win-10.png",y="/shell/termius/win/win-11.png",b="/shell/termius/ios/ios-02.png",z="/shell/termius/ios/ios-03.png",f="/shell/termius/ios/ios-04.png",k="/shell/termius/win/win-12.png",v="/shell/termius/win/win-13.png",T="/shell/termius/win/win-14.png",P="/shell/termius/win/win-15.png",q="/shell/termius/win/win-16.png",x="/shell/termius/win/win-17.png",S="/shell/termius/win/win-18.png",C="/shell/termius/win/win-19.png",H="/shell/termius/win/win-20.png",O="/shell/termius/win/win-21.png",U=JSON.parse('{"title":"Termius的安装及使用","description":"","frontmatter":{},"headers":[],"relativePath":"yiov/website/shell/Termius.md","filePath":"yiov/website/shell/Termius.md","lastUpdated":1734431113000}'),W={name:"yiov/website/shell/Termius.md"};function $(E,s,N,A,B,D){return l(),e("div",null,s[0]||(s[0]=[t('<h1 id="termius的安装及使用" tabindex="-1">Termius的安装及使用 <a class="header-anchor" href="#termius的安装及使用" aria-label="Permalink to &quot;Termius的安装及使用&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">(当前界面文档全部来自yiov.top)</p><ul><li><a href="https://yiov.top/" target="_blank" rel="noreferrer">yiov</a>「伊奥&#39;s 教程集」</li></ul></div><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p>一款强大的跨平台 SSH 工具，支持Windows、iOS、Mac、Android、Linux系统</p><p>官网：<a href="https://www.termius.com/" target="_blank" rel="noreferrer">https://www.termius.com/</a></p><p>仓库：<a href="https://github.com/termius" target="_blank" rel="noreferrer">https://github.com/termius</a></p><p><img src="'+a+'" alt="" loading="lazy"></p><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><p>在 <a href="https://www.termius.com/download/" target="_blank" rel="noreferrer">官网下载</a> 对应的客户端即可</p><p><img src="'+o+'" alt="" loading="lazy"></p><details class="details custom-block"><summary>iOS端安装</summary><p>在 Appstore 搜索并下载 <a href="https://apps.apple.com/cn/app/id549039908" target="_blank" rel="noreferrer">termius</a></p><p><img src="'+r+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>windows端安装</summary><p>在官网 <a href="https://www.termius.com/download/windows" target="_blank" rel="noreferrer">Termius下载页</a> ，点击 <code>Download.EXE</code> 下载</p><p><img src="'+p+'" alt="" loading="lazy"></p><p>下载完成后，点击 <code>Install Termius.exe</code> 自动安装</p><p><img src="'+n+'" alt="" loading="lazy"></p><p>安装好后打开 Termius</p><p><img src="'+m+'" alt="" loading="lazy"></p><p><img src="'+c+'" alt="" loading="lazy"></p><p>点击 <code>set up your vault</code> 设置账户</p><p><img src="'+d+'" alt="" loading="lazy"></p><p>点击 <code>Connect with email</code> 用邮箱注册</p><p><img src="'+u+'" alt="" loading="lazy"></p><p>设置你的密码</p><p><img src="'+g+'" alt="" loading="lazy"></p><p>这里邀请团队和导入数据，按需添加，我直接点 <code>later</code></p><p><img src="'+h+'" alt="" loading="lazy"></p><p><img src="'+_+'" alt="" loading="lazy"></p><p>最后，点 <code>Start connecting</code> 开始连接</p><p><img src="'+w+'" alt="" loading="lazy"></p><p><img src="'+y+'" alt="" loading="lazy"></p></details><h2 id="使用" tabindex="-1">使用 <a class="header-anchor" href="#使用" aria-label="Permalink to &quot;使用&quot;">​</a></h2><details class="details custom-block"><summary>iOS端使用</summary><p>打开，点击 <code>Hosts</code> ，再点右上角 <code>+</code></p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>可以不登录账号使用，登录只是方便同步信息</p></div><p><img src="'+b+'" alt="" loading="lazy"></p><p>点 <code>New Host</code> ，填写信息后，save保存</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p><code>host name</code> 填公网IP</p><p><code>Username</code> 填账户名 <code>root</code></p><p><code>Password</code> 填服务器密码</p></div><p><img src="'+z+'" alt="" loading="lazy"></p><p>这样就登录成功了，点击当前标签也可关闭连接</p><p><img src="'+f+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>windows端使用及汉化</summary><p>我们使用 <a href="https://github.com/WizisCool/Termius-Chinese-Patch" target="_blank" rel="noreferrer">Termius-Chinese-Patch</a> 做好的汉化</p><p>点击下载</p><p><img src="'+k+'" alt="" loading="lazy"></p><p>右边有下载按钮</p><p><img src="'+v+'" alt="" loading="lazy"></p><p>解压，输入解压密码</p><p><img src="'+T+'" alt="" loading="lazy"></p><p>鼠标对着 Termius 右键 - 属性 - 打开文件安装位置</p><p><img src="'+P+'" alt="" loading="lazy"></p><p>进入 <code>resources</code> 文件夹，将解压的 <code>app.asar</code> 复制到安装目录替换</p><p><img src="'+q+'" alt="" loading="lazy"></p><p>重新打开 Termius，基本上已经汉化好了，其他已经能看懂了</p><p><img src="'+x+'" alt="" loading="lazy"></p><p>点击创建主机</p><p><img src="'+S+'" alt="" loading="lazy"></p><p>地址处输入公网ip，SSH处输入账号密码，连接</p><p><img src="'+C+'" alt="" loading="lazy"></p><p>添加并继续</p><p><img src="'+H+'" alt="" loading="lazy"></p><p>这样就连接成功了，右侧可以选择背景色及字体大小</p><p><img src="'+O+'" alt="" loading="lazy"></p></details><h2 id="特别鸣谢" tabindex="-1">特别鸣谢 <a class="header-anchor" href="#特别鸣谢" aria-label="Permalink to &quot;特别鸣谢&quot;">​</a></h2><ul><li><a href="https://github.com/WizisCool/Termius-Chinese-Patch" target="_blank" rel="noreferrer">@WizisCool：Termius-Chinese-Patch</a></li></ul>',17)]))}const J=i(W,[["render",$]]);export{U as __pageData,J as default};
