import{_ as s,c as p,a4 as e,o as l}from"./chunks/framework.CyeRpf1z.js";const t="/taobao/taobao-01.png",r="/taobao/taobao-02.png",i="/taobao/taobao-03.png",o="/taobao/taobao-04.png",c="/taobao/taobao-05.png",n="/taobao/taobao-06.png",d="/taobao/taobao-07.png",h="/taobao/taobao-08.png",m="/taobao/taobao-09.png",b="/taobao/taobao-10.png",g="/taobao/taobao-11.png",u="/taobao/taobao-12.png",k="/taobao/taobao-13.png",_="/taobao/taobao-14.png",y="/taobao/taobao-15.png",f="/taobao/taobao-16.png",C=JSON.parse('{"title":"淘宝商品强制提交","description":"","frontmatter":{},"headers":[],"relativePath":"yiov/daily/taobao.md","filePath":"yiov/daily/taobao.md","lastUpdated":1734431113000}'),v={name:"yiov/daily/taobao.md"};function P(x,a,z,q,I,B){return l(),p("div",null,a[0]||(a[0]=[e('<h1 id="淘宝商品强制提交" tabindex="-1">淘宝商品强制提交 <a class="header-anchor" href="#淘宝商品强制提交" aria-label="Permalink to &quot;淘宝商品强制提交&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">(当前界面文档来自yiov.top)</p><ul><li><a href="https://yiov.top/" target="_blank" rel="noreferrer">yiov</a>「伊奥&#39;s 教程集」</li></ul></div><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p>适用于无货商品、秒杀商品，当别人都在挤时，你已经进去提交了，也就是常见的BP链接</p><p>如果你不想动手，可以看一下实现的基本逻辑</p><p>最后直接使用 <a href="#快捷指令">快捷指令</a> 快速达成</p><h2 id="淘口令解析" tabindex="-1">淘口令解析 <a class="header-anchor" href="#淘口令解析" aria-label="Permalink to &quot;淘口令解析&quot;">​</a></h2><p>其实在浏览器打开链接，即可获取原始链接了</p><p>随便找个商品，复制链接，比如：<a href="https://m.tb.cn/h.gf4xVnVN5QOdUsq?tk=Vfz1WyO2tcK" target="_blank" rel="noreferrer">雅兰儿童床垫蘭精灵</a></p><p><img src="'+t+'" alt="" loading="lazy"></p><p>本次借用一个在线网页工具：<a href="http://m.91tool.com/tkljx" target="_blank" rel="noreferrer">9tool - 淘口令解析</a></p><p>粘贴到这里，解析口令，原始链接和商品ID都有了</p><p><img src="'+r+'" alt="" loading="lazy"></p><h2 id="发送商品" tabindex="-1">发送商品 <a class="header-anchor" href="#发送商品" aria-label="Permalink to &quot;发送商品&quot;">​</a></h2><p>适用于聊天窗直接购买，但是需要手动选取商品规格</p><p>格式：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://h5.m.taobao.com/awp/core/detail.htm?xxc=sendProduct&amp;id={商品ID}</span></span></code></pre></div><p>比如我的 <a href="#淘口令解析">商品ID</a> 是：<code>628435603375</code> ，那组合后就是</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://h5.m.taobao.com/awp/core/detail.htm?xxc=sendProduct&amp;id=628435603375</span></span></code></pre></div><p><img src="'+i+'" alt="" loading="lazy"></p><h2 id="单件闪结" tabindex="-1">单件闪结 <a class="header-anchor" href="#单件闪结" aria-label="Permalink to &quot;单件闪结&quot;">​</a></h2><p>除了需要商品ID，还有一个重要参数 <code>skuId</code></p><p>电脑端是点击商品规格后就可以获取，手机端就麻烦点了</p><details class="details custom-block"><summary>自动获取skuId 并 生成BP链接 （推荐👍）</summary><p>下载安装 <a href="https://apps.apple.com/cn/app/id1478077079" target="_blank" rel="noreferrer">探火</a> APP，并打开</p><p><img src="'+o+'" alt="" loading="lazy"></p><p>随便找个商品，复制链接，比如：<a href="https://m.tb.cn/h.gf4xVnVN5QOdUsq?tk=Vfz1WyO2tcK" target="_blank" rel="noreferrer">雅兰儿童床垫蘭精灵</a></p><p><img src="'+t+'" alt="" loading="lazy"></p><p>探火APP - 秒杀- 闪结链接，粘贴淘宝口令 - 查询</p><p><img src="'+c+'" alt="" loading="lazy"></p><p>选择自己要买的商品型号，<code>生成BP</code>，选 <code>淘宝</code>，复制全部</p><p><img src="'+n+'" alt="" loading="lazy"></p><p>点击链接，即可进入购买的提交结算页面</p><p><img src="'+d+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>手动获取skuId 并 拼接BP链接</summary><p>请先在淘宝APP将要购买的东西加入购物车，比如：<a href="https://m.tb.cn/h.gf4xVnVN5QOdUsq?tk=Vfz1WyO2tcK" target="_blank" rel="noreferrer">雅兰儿童床垫蘭精灵</a></p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>无法加购的商品，请使用 <a href="#单件闪结">自动获取</a></p></div><p><img src="'+h+'" alt="" loading="lazy"></p><p>浏览器打开 <a href="https://main.m.taobao.com/cart/index.html" target="_blank" rel="noreferrer">淘宝购物车</a> 页面并登录，点击购物车中的商品进入</p><p><img src="'+m+'" alt="" loading="lazy"></p><p>复制链接到其他聊天工具，将skuId复制出来即可</p><div class="danger custom-block"><p class="custom-block-title">注意</p><p>别发到淘宝聊天窗，会自动被识别成卡片！</p></div><p><img src="'+b+'" alt="" loading="lazy"></p><p>现在开始拼接BP链接，格式：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://h5.m.taobao.com/cart/order.html?buyParam={商品ID}_{购买数量}_{skuId}</span></span></code></pre></div><p>按格式组成后：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://h5.m.taobao.com/cart/order.html?buyParam=628435603375_1_5292744355844</span></span></code></pre></div><p><img src="'+g+'" alt="" loading="lazy"></p></details><h2 id="多件闪结" tabindex="-1">多件闪结 <a class="header-anchor" href="#多件闪结" aria-label="Permalink to &quot;多件闪结&quot;">​</a></h2><p>其实就是挨个提取商品ID和skuId，然后组合</p><details class="details custom-block"><summary>自动拼接 多件商品BP链接（推荐👍）</summary><p>依然推荐用 <a href="https://apps.apple.com/cn/app/id1478077079" target="_blank" rel="noreferrer">探火</a> APP</p><p><img src="'+o+'" alt="" loading="lazy"></p><p>先选第1个商品复制链接，比如：<a href="https://m.tb.cn/h.gf4xVnVN5QOdUsq?tk=Vfz1WyO2tcK" target="_blank" rel="noreferrer">雅兰儿童床垫蘭精灵</a></p><p>到探火APP - 秒杀 - 闪付链接，粘贴口令 - 查询，选择规格后加入购物车</p><p><img src="'+u+'" alt="" loading="lazy"></p><p>再选第2个商品复制链接，比如：<a href="https://m.tb.cn/h.gUrhQiHgACjaIb6?tk=fCNeWyN6hTu" target="_blank" rel="noreferrer">万代模型 RG 1/144 强攻型脉冲高达</a></p><p>到探火APP - 秒杀 - 闪付链接，粘贴口令 - 查询，选择规格后加入购物车</p><p><img src="'+k+'" alt="" loading="lazy"></p><p>回到秒杀界面，顶部 <code>淘宝购物车</code> ，全选，生成BP</p><p><img src="'+_+'" alt="" loading="lazy"></p><p>选择淘宝BP，复制BP链接</p><p><img src="'+y+'" alt="" loading="lazy"></p><p>粘贴到淘宝对话框，打开即可</p><p><img src="'+f+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>手动拼接 多件商品BP链接</summary><p>需要先获取单个商品id和skuId，然后按格式拼接</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://h5.m.taobao.com/cart/order.html?buyParam=&lt;商品1的ID&gt;_&lt;商品1的数量&gt;_&lt;商品1的skuid&gt;,&lt;商品2的ID&gt;_&lt;商品2的数量&gt;_&lt;商品2的skuid&gt;</span></span></code></pre></div></details><h2 id="快捷指令" tabindex="-1">快捷指令 <a class="header-anchor" href="#快捷指令" aria-label="Permalink to &quot;快捷指令&quot;">​</a></h2><ul><li><a href="https://www.icloud.com/shortcuts/2f3d6dd7acd04cccb217ea96485cca30" target="_blank" rel="noreferrer">淘宝BP链接</a></li></ul>',31)]))}const D=s(v,[["render",P]]);export{C as __pageData,D as default};
