import{_ as t,c as s,a4 as i,o as e}from"./chunks/framework.CyeRpf1z.js";const r="/NekoBox/NekoBox.png",p="/NekoBox/android/android-01.png",l="/NekoBox/windows/windows-01.png",n="/NekoBox/windows/windows-02.png",d="/NekoBox/windows/windows-03.png",c="/NekoBox/windows/windows-04.png",m="/NekoBox/android/android-02.png",g="/NekoBox/android/android-03.png",k="/NekoBox/android/android-04.png",w="/NekoBox/android/android-05.png",h="/NekoBox/android/android-06.png",_="/NekoBox/android/android-07.png",u="/NekoBox/android/android-08.png",y="/NekoBox/windows/windows-05.png",x="/NekoBox/windows/windows-06.png",b="/NekoBox/windows/windows-07.png",N="/NekoBox/windows/windows-08.png",B="/NekoBox/windows/windows-09.png",f="/NekoBox/windows/windows-10.png",z="/NekoBox/windows/windows-11.png",P="/NekoBox/windows/windows-12.png",v="/NekoBox/windows/windows-13.png",q="/NekoBox/windows/windows-14.png",a="/NekoBox/windows/windows-15.png",I="/NekoBox/android/android-09.png",W="/NekoBox/android/android-10.png",A="/NekoBox/android/android-11.png",S="/NekoBox/windows/windows-16.png",T="/NekoBox/windows/windows-17.png",M="/NekoBox/windows/windows-18.png",V="/NekoBox/windows/windows-19.png",C="/NekoBox/windows/windows-20.png",G="/NekoBox/android/android-12.png",L="/NekoBox/android/android-13.png",D="/NekoBox/android/android-14.png",R="/NekoBox/windows/windows-21.png",U="/NekoBox/windows/windows-22.png",F="/NekoBox/windows/windows-23.png",H="/NekoBox/windows/windows-24.png",$="/NekoBox/windows/windows-25.png",j="/NekoBox/windows/windows-26.png",E="/NekoBox/windows/windows-27.png",O="/NekoBox/windows/windows-28.png",Y="/NekoBox/windows/windows-29.png",J="/NekoBox/android/android-15.png",K="/NekoBox/android/android-16.png",Q="/NekoBox/android/android-17.png",X="/NekoBox/android/android-18.png",po=JSON.parse('{"title":"NekoBox的安装及使用","description":"","frontmatter":{},"headers":[],"relativePath":"yiov/gfw/NekoBox.md","filePath":"yiov/gfw/NekoBox.md","lastUpdated":1734431113000}'),Z={name:"yiov/gfw/NekoBox.md"};function oo(ao,o,to,so,io,eo){return e(),s("div",null,o[0]||(o[0]=[i('<h1 id="nekobox的安装及使用" tabindex="-1">NekoBox的安装及使用 <a class="header-anchor" href="#nekobox的安装及使用" aria-label="Permalink to &quot;NekoBox的安装及使用&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">(当前界面文档来自yiov.top)</p><ul><li><a href="https://yiov.top/" target="_blank" rel="noreferrer">yiov</a>「伊奥&#39;s 教程集」</li></ul></div><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p>NekoBox for Android，简称 <code>NB4A</code>，是一款基于 <a href="./singbox.html">sing-box内核</a> 开发的安卓端代理工具</p><p>官网：<a href="https://matsuridayo.github.io/" target="_blank" rel="noreferrer">https://matsuridayo.github.io/</a></p><p><img src="'+r+'" alt="" loading="lazy"></p><h2 id="客户端" tabindex="-1">客户端 <a class="header-anchor" href="#客户端" aria-label="Permalink to &quot;客户端&quot;">​</a></h2><ul><li><p><a href="https://github.com/MatsuriDayo/NekoBoxForAndroid/releases" target="_blank" rel="noreferrer">安卓端</a></p></li><li><p><a href="https://github.com/MatsuriDayo/nekoray/releases" target="_blank" rel="noreferrer">Windows端</a></p></li><li><p><a href="https://github.com/MatsuriDayo/nekoray/releases" target="_blank" rel="noreferrer">Linux端</a></p></li></ul><h2 id="协议" tabindex="-1">协议 <a class="header-anchor" href="#协议" aria-label="Permalink to &quot;协议&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:center;">类型</th><th style="text-align:center;">支持协议</th></tr></thead><tbody><tr><td style="text-align:center;">节点</td><td style="text-align:center;">SOCKS、HTTP、Shadowsocks、VMess、VLESS、Trojan、Trojan-Go、Mieru、Naïve、Hysterria、TUIC、SSH、shadowTLS、WireGuard</td></tr><tr><td style="text-align:center;">订阅</td><td style="text-align:center;">Shadowsocks、V2RayN、Clash、Clash Meta、Sing-box</td></tr></tbody></table><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><details class="details custom-block"><summary>Android端 安装</summary><p>下载并安装 <a href="https://github.com/MatsuriDayo/NekoBoxForAndroid/releases" target="_blank" rel="noreferrer">NekoBox</a>，我直接下第一个演示</p><details class="details custom-block"><summary>下载哪个？</summary><ul><li><p>arm64-v8a：64位处理器，常见于市面主流手机</p></li><li><p>armeabi-v7a：32位处理器，常见于老旧款手机</p></li><li><p>x86：32位处理器，常见于平板和模拟器</p></li><li><p>x86_64：64位处理器，常见于平板</p></li></ul></details><p><img src="'+p+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>Windows端 Nekoray 安装</summary><p>下载 <a href="https://github.com/MatsuriDayo/nekoray/releases" target="_blank" rel="noreferrer">Nekoray</a>，选择Windows端下载并解压</p><p><img src="'+l+'" alt="" loading="lazy"></p><p>双击 <code>nekoray.exe</code> 打开</p><p><img src="'+n+'" alt="" loading="lazy"></p><p>首次打开需要选择内核，推荐 <code>sing-box</code></p><p><img src="'+d+'" alt="" loading="lazy"></p><p><img src="'+c+'" alt="" loading="lazy"></p></details><h2 id="使用" tabindex="-1">使用 <a class="header-anchor" href="#使用" aria-label="Permalink to &quot;使用&quot;">​</a></h2><details class="details custom-block"><summary>Android端 使用</summary><p>找一个免费送流量的机场注册，<a href="./channel.html">点我查看：机场推荐指南</a></p><p>一键订阅 - 复制订阅地址，由于没有快速跳转，我们手动导入订阅</p><p><img src="'+m+'" alt="" loading="lazy"></p><p>打开NekoBox左上角三横 <code>≡</code> - <code>分组</code> - 右上角 <code>添加</code></p><p><img src="'+g+'" alt="" loading="lazy"></p><p>分组名自己随便写，分组类型改成 <code>订阅</code></p><p>在订阅链接处粘贴订阅地址，右上角保存</p><p><img src="'+k+'" alt="" loading="lazy"></p><p>等待自动导入成功即可，不要了直接左滑删除</p><p><img src="'+w+'" alt="" loading="lazy"></p><p>先点右上角三点 <code>┇</code> - 连接测试 - <code>URL Test</code> 测速</p><p><img src="'+h+'" alt="" loading="lazy"></p><p>为了避免后续导入节点使用问题，在设置 - 入站设置，打开 <code>追加HTTP代理至VPN</code> 开关</p><p><img src="'+_+'" alt="" loading="lazy"></p><p>还可以按节点按延时最低的排序，选最低的，点击底部连接</p><p>访问 <a href="https://www.google.com" target="_blank" rel="noreferrer">谷歌</a> 成功即可</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>点击底部 测试 ，可以查看成否成功握手</p></div><p><img src="'+u+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>Windows端 Nekoray 使用</summary><p>在 <a href="./channel.html">机场推荐指南</a> 中找一个白嫖机场注册</p><p>一键订阅 - 复制订阅地址，由于没有快速跳转，我们手动导入订阅</p><p><img src="'+y+'" alt="" loading="lazy"></p><p>首选项 - 分组</p><p><img src="'+x+'" alt="" loading="lazy"></p><p>新建分组</p><p><img src="'+b+'" alt="" loading="lazy"></p><p>名称随便，类型选 <code>订阅</code> ，URL粘贴 <code>订阅链接</code>，确定</p><p>可勾选手动调节列宽</p><p><img src="'+N+'" alt="" loading="lazy"></p><p>更新订阅</p><p><img src="'+B+'" alt="" loading="lazy"></p><p>出现剩余流量等信息即可</p><p><img src="'+f+'" alt="" loading="lazy"></p><p>点击刚才的机场名查看节点</p><p><img src="'+z+'" alt="" loading="lazy"></p><p>服务器 - 当前分组 - Url Test 测速</p><p><img src="'+P+'" alt="" loading="lazy"></p><p>选择一个延迟低一点的节点 <code>启动</code></p><p><img src="'+v+'" alt="" loading="lazy"></p><p>勾选 <code>系统代理</code> ，开启代理</p><details class="details custom-block"><summary>不使用了?</summary><p>取消系统代理的勾选，节点停止</p></details><p><img src="'+q+'" alt="" loading="lazy"></p><p>访问 <a href="https://www.google.com" target="_blank" rel="noreferrer">谷歌</a> 成功即可</p><p><img src="'+a+'" alt="" loading="lazy"></p></details><h2 id="分流" tabindex="-1">分流 <a class="header-anchor" href="#分流" aria-label="Permalink to &quot;分流&quot;">​</a></h2><p>简而言之，就是访问国内网页，不走代理，走直连；</p><p>访问国外网页，走代理</p><details class="details custom-block"><summary>Android端 分流</summary><p>在 <code>设置</code> 中，开启分应用代理、绕过局域网地址、在核心中绕过LAN、IPv6路由启用</p><div class="tip custom-block"><p class="custom-block-title">开启有什么用</p><ul><li><p>分应用代理：可以指定某些APP是否走代理</p></li><li><p>绕过局域网地址：开启后可以访问本地路由器 (和绕过LAN一样)</p></li></ul></div><p><img src="'+I+'" alt="" loading="lazy"></p><p>在路由中开启 <code>中国域名规则</code> 和 <code>中国IP规则</code></p><p><img src="'+W+'" alt="" loading="lazy"></p><p>现在访问国内网址就不用走代理了，我们测试下是否分流成功</p><p>IP分流查询：<a href="https://ip.skk.moe/" target="_blank" rel="noreferrer">https://ip.skk.moe/</a></p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>左侧为Wireguard客户端，右侧为NekoBox客户端</p><p>可以发现NekoBox访问国内并没有走代理，走的直连，非常好用</p></div><p><img src="'+A+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>Winddows端 分流</summary><p>首选项 - 路由设置</p><p><img src="'+S+'" alt="" loading="lazy"></p><p>简易路由 - 预设 - 绕过局域网和大陆</p><p><img src="'+T+'" alt="" loading="lazy"></p><p>开启成功，会自动填入一些规则信息</p><p><img src="'+M+'" alt="" loading="lazy"></p><p>设置改变重启代理</p><p><img src="'+V+'" alt="" loading="lazy"></p><p>IP分流查询：<a href="https://ip.skk.moe/" target="_blank" rel="noreferrer">https://ip.skk.moe/</a></p><div class="tip custom-block"><p class="custom-block-title">解读</p><ul><li><p>国内的走直连，是本地IP</p></li><li><p>国外的走代理，是香港节点服务器</p></li></ul></div><p><img src="'+C+'" alt="" loading="lazy"></p></details><h2 id="优选ip" tabindex="-1">优选IP <a class="header-anchor" href="#优选ip" aria-label="Permalink to &quot;优选IP&quot;">​</a></h2><p>对于分享的延迟较高的节点，我们可以通过优选IP来降低延迟</p><details class="details custom-block"><summary>Android端 优选IP</summary><p>我这里准备了一个 <a href="https://t.me/binghequanzi" target="_blank" rel="noreferrer">@冰河圈子</a> 分享的Vmess节点，复制粘贴进来后，点编辑</p><p><img src="'+G+'" alt="" loading="lazy"></p><p>不要挂任何代理，打开 <a href="https://stock.hostmonit.com/CloudFlareYes" target="_blank" rel="noreferrer">在线优选IP（默认443端口）</a> ，从自动筛选的结果里，挑选自己运营商的IP，复制</p><p>然后打开V2RayNG，地址这里删掉之前的，粘贴复制的IP，保存</p><p><img src="'+L+'" alt="" loading="lazy"></p><p>我们点击运行，访问 <a href="https://www.google.com" target="_blank" rel="noreferrer">谷歌</a> 看看</p><p><img src="'+D+'" alt="" loading="lazy"></p></details><details class="details custom-block"><summary>Windows端 优选IP</summary><p>我这里准备了一个 <a href="https://t.me/binghequanzi" target="_blank" rel="noreferrer">@冰河圈子</a> 分享的Vmess节点</p><p><img src="'+R+'" alt="" loading="lazy"></p><p>复制后，点 程序 - 从剪切板添加</p><p><img src="'+U+'" alt="" loading="lazy"></p><p>这样就添加成功了，双击这个节点</p><p><img src="'+F+'" alt="" loading="lazy"></p><p>我们要更改这里的地址</p><p><img src="'+H+'" alt="" loading="lazy"></p><p>不要挂任何代理，打开 <a href="https://stock.hostmonit.com/CloudFlareYes" target="_blank" rel="noreferrer">在线优选IP（默认443端口）</a> ，从自动筛选的结果里，挑选自己运营商的IP，复制</p><p><img src="'+$+'" alt="" loading="lazy"></p><p>地址这里删掉之前的，粘贴复制的IP，确定</p><p><img src="'+j+'" alt="" loading="lazy"></p><p>地址就变成了我们修改的</p><p><img src="'+E+'" alt="" loading="lazy"></p><p>选择节点 - 启动</p><p><img src="'+O+'" alt="" loading="lazy"></p><p>勾选系统代理</p><p><img src="'+Y+'" alt="" loading="lazy"></p><p>访问 <a href="https://www.google.com" target="_blank" rel="noreferrer">谷歌</a> 看看</p><p><img src="'+a+'" alt="" loading="lazy"></p></details><h2 id="拓展" tabindex="-1">拓展 <a class="header-anchor" href="#拓展" aria-label="Permalink to &quot;拓展&quot;">​</a></h2><details class="details custom-block"><summary>WARP (WireGuard)</summary><p>NekoBox可一键生成WireGuard配置，仅限安卓端</p><p>NekoBox左上角三横 <code>≡</code> - <code>工具</code></p><p><img src="'+J+'" alt="" loading="lazy"></p><p>在 Cloudflare Warp 处点击 <code>生成配置</code> 即可</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>移动和联通用户大概率无法连接，需要优选IP</p></div><p><img src="'+K+'" alt="" loading="lazy"></p><p>请查看 <a href="./wireguard.html#优选ip">WireGuard优选IP的教程</a> ，更改服务器IP和端口</p><p>也可以自行更换公钥和私钥</p><p><img src="'+Q+'" alt="" loading="lazy"></p><p>更改完后访问 <a href="https://www.google.com" target="_blank" rel="noreferrer">谷歌</a> 成功即可</p><div class="tip custom-block"><p class="custom-block-title">说明</p><p>但是这种转发速度比WireGuard要慢，推荐使用官方的客户端</p></div><p><img src="'+X+'" alt="" loading="lazy"></p></details><h2 id="特别鸣谢" tabindex="-1">特别鸣谢 <a class="header-anchor" href="#特别鸣谢" aria-label="Permalink to &quot;特别鸣谢&quot;">​</a></h2><ul><li><a href="https://ip.skk.moe/" target="_blank" rel="noreferrer">IP分流查询</a></li></ul>',29)]))}const lo=t(Z,[["render",oo]]);export{po as __pageData,lo as default};
