import{_ as e,c as r,a4 as a,o as l}from"./chunks/framework.CyeRpf1z.js";const n="/server/server-01.png",x=JSON.parse('{"title":"服务器的介绍","description":"","frontmatter":{},"headers":[],"relativePath":"yiov/website/Server/index.md","filePath":"yiov/website/Server/index.md","lastUpdated":1734431113000}'),d={name:"yiov/website/Server/index.md"};function o(s,t,i,c,h,b){return l(),r("div",null,t[0]||(t[0]=[a('<h1 id="服务器的介绍" tabindex="-1">服务器的介绍 <a class="header-anchor" href="#服务器的介绍" aria-label="Permalink to &quot;服务器的介绍&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">(当前界面文档全部来自yiov.top)</p><ul><li><a href="https://yiov.top/" target="_blank" rel="noreferrer">yiov</a>「伊奥&#39;s 教程集」</li></ul></div><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p>计算机的一种，具有高速的CPU运算能力、长时间的可靠运行、强大的I/O外部数据吞吐能力以及更好的扩展性</p><p>简单来说它也一台电脑，同样有电脑的硬件：<code>CPU</code>、<code>主板</code>、<code>硬盘</code>、<code>内存</code>等</p><p>从外观上分为：机架式、刀片式、塔式、机柜式</p><p><img src="'+n+'" alt="" loading="lazy"></p><h2 id="区别" tabindex="-1">区别 <a class="header-anchor" href="#区别" aria-label="Permalink to &quot;区别&quot;">​</a></h2><blockquote><p>对于建站而言，我们常说的服务器，一般是指VPS</p></blockquote><table tabindex="0"><thead><tr><th style="text-align:center;">类型</th><th style="text-align:center;">说明</th></tr></thead><tbody><tr><td style="text-align:center;">独立服务器</td><td style="text-align:center;">一般指的是物理服务器，比如：常说的机房</td></tr><tr><td style="text-align:center;">云服务器</td><td style="text-align:center;">一般指的是弹性服务器，可以自由的改变服务器的硬件，比如：阿里云的ECS 和 腾讯云的CVM</td></tr><tr><td style="text-align:center;">VPS</td><td style="text-align:center;">Virtual Private Server，简称VPS，通过虚拟技术，将独立服务器分隔成多个虚拟专用服务器，不可以自由改变服务器硬件，只能根据自己需求购买对应的配置</td></tr><tr><td style="text-align:center;">虚拟主机</td><td style="text-align:center;">Web Hosting，又叫主机空间，多个用户共用一份VPS资源，有共享IP和独立IP，而独立IP价格贵，不如直接买VPS</td></tr></tbody></table><h2 id="收费服务器" tabindex="-1">收费服务器 <a class="header-anchor" href="#收费服务器" aria-label="Permalink to &quot;收费服务器&quot;">​</a></h2><blockquote><p>带 ⭐ 的是有教程的，点击即可跳转</p><p>服务器商商不计其数，仅列举常用的！</p></blockquote><table tabindex="0"><thead><tr><th style="text-align:center;">国内</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:center;"><a href="https://aliyun.com/" target="_blank" rel="noreferrer">万网·阿里云</a></td><td style="text-align:left;">🟢 阿里旗下业务，国内主流服务器提供商<br>🟠 国内服务器需要备案</td></tr><tr><td style="text-align:center;">⭐<a href="./tencent.html">腾讯云</a></td><td style="text-align:left;">🟢 腾讯旗下业务，国内主流服务器提供商<br>🟠 国内服务器需要备案</td></tr><tr><td style="text-align:center;"></td><td style="text-align:left;"></td></tr><tr><td style="text-align:center;"><strong>国际</strong></td><td style="text-align:left;"><strong>说明</strong></td></tr><tr><td style="text-align:center;"><a href="https://bandwagonhost.com/" target="_blank" rel="noreferrer">BandwagonHost</a><br><a href="https://bwh89.net/" target="_blank" rel="noreferrer">搬瓦工镜像站</a></td><td style="text-align:left;">🟢 隶属IT7公司旗下，最受欢迎的国外VPS提供商之一，价格相对较低，且对中国的线路优化<br>🟠 遇到问题需要提交工单，使用英文，回复时间慢</td></tr><tr><td style="text-align:center;"><a href="https://www.vultr.com/" target="_blank" rel="noreferrer">Vultr</a></td><td style="text-align:left;">🟢 隶属choopa旗下，最受欢迎的国外VPS提供商之一，可免费换IP，且客服响应迅速<br>🟠 未对中国的线路优化</td></tr></tbody></table><h2 id="免费服务器" tabindex="-1">免费服务器 <a class="header-anchor" href="#免费服务器" aria-label="Permalink to &quot;免费服务器&quot;">​</a></h2><blockquote><p>免费=最贵的，实际是贵的你需要花费的时间成本</p></blockquote><table tabindex="0"><thead><tr><th style="text-align:center;">服务器</th><th style="text-align:center;">说明</th></tr></thead><tbody><tr><td style="text-align:center;">⭐<a href="./../VMware.html">虚拟机</a></td><td style="text-align:center;">🟢 通过本地搭建虚拟机，模拟Windows、Linux、macOS等系统，当做服务器使用<br>🟠 需要一直保持开启状态，比较占用电脑内存</td></tr><tr><td style="text-align:center;">⭐<a href="./../pages/">静态托管</a></td><td style="text-align:center;">🟢 利用github等仓库进行托管部署，免费白嫖<br>🟠 网站有一定延迟，纯静态无数据库</td></tr><tr><td style="text-align:center;">⭐<a href="./Serv00.html">Serv00</a></td><td style="text-align:center;">🟢 512MB+3GB容量，不限单个文件大小，不限流量，有效期10年<br>🟠 页面稍微有点卡，但是不影响使用</td></tr><tr><td style="text-align:center;">⭐<a href="./wordpress.html">wordpress主机</a></td><td style="text-align:center;">🟢 1GB容量，无限带宽，无限有效期<br>🟠 wordpress祖传的卡，不能上传主题，可玩性低</td></tr><tr><td style="text-align:center;">⭐<a href="./wordpress.html">Hax</a></td><td style="text-align:center;">🟢 1核450M，5G容量，无限流量，7天有效期<br>🟠 忘记续期会被释放，申请需要抢，比较困难</td></tr></tbody></table>',16)]))}const y=e(d,[["render",o]]);export{x as __pageData,y as default};
